function(){
$A.componentService.addComponentClass("aura$component", function() {



    var aura$component = function aura$component(config, localCreation, creatingPrototype) {
        $A.Component.call(this, config, localCreation, creatingPrototype); 
    };
    aura$component.prototype = new $A.Component(null, null, true);
    aura$component.prototype.constructor = aura$component;

    aura$component.Helper = function() {};

    aura$component.prototype.helper = new aura$component.Helper();


    aura$component.prototype.render = function() {
        var renderable = this.getRenderable();
        var component = renderable;

        var rendering = component.getRendering();
        return rendering||$A.renderingService.renderFacet(component,component.get("v.body"));

    };

    aura$component.prototype.rerender = function() {
        var renderable = this.getRenderable();
        var component = renderable;

        var body = component.get("v.body");
        return $A.renderingService.rerenderFacet(component,body);

    };

    aura$component.prototype.afterRender = function() {
        var renderable = this.getRenderable();
        var component = renderable;

        var body = component.get("v.body");
        $A.afterRender(body);

    };

    aura$component.prototype.unrender = function() {
        var renderable = this.getRenderable();
        var component = renderable;

        var body = component.get("v.body");
        $A.renderingService.unrenderFacet(component,body);

    };



    $A.Component.registerMethods(aura$component);

    return aura$component;
});


}

function(){
$A.componentService.addComponentClass("aura$text", function() {


    var aura$component = $A.componentService.getComponentClass("aura$component");


    var aura$text = function aura$text(config, localCreation, creatingPrototype) {
        aura$component.call(this, config, localCreation, creatingPrototype); 
    };
    aura$text.prototype = new aura$component(null, null, true);
    aura$text.prototype.constructor = aura$text;

    aura$text.Helper = function() {};

    aura$text.Helper.prototype = new aura$component.Helper();
    aura$text.Helper.prototype.constructor = aura$text.Helper;
    aura$text.prototype.helper = new aura$text.Helper();


    aura$text.prototype.render = function() {
        var renderable = this.getRenderable();
        var component = renderable;

        var value = component.get("v.value");

        var trunc = component.get("v.truncate");
        var truncateByWord = $A.util.getBooleanValue(component.get("v.truncateByWord"));
        var ellipsis = $A.util.getBooleanValue(component.get("v.ellipsis"));

        if(trunc){
            trunc = 1 * trunc;
            value = aura.util.truncate(value, trunc, ellipsis, truncateByWord);
        }
        return [document.createTextNode($A.util.isUndefinedOrNull(value)?'':value)];

    };

    aura$text.prototype.rerender = function() {
        var renderable = this.getRenderable();
        var component = renderable;

        var element=component.getElement();

        if (element && element.parentNode) {
        	var textValue = component.get("v.value");
            textValue = $A.util.isUndefinedOrNull(textValue) ? '' : textValue;

            if (element.nodeValue !== textValue) {
                element.nodeValue = textValue;
            }
        }

    };

    aura$text.prototype.afterRender = function() {
        var zuper = this.getSuper();
        return zuper ? zuper.afterRender() : undefined;
    };
    aura$text.prototype.unrender = function() {
        var zuper = this.getSuper();
        return zuper ? zuper.unrender() : undefined;
    };


    $A.Component.registerMethods(aura$text);

    return aura$text;
});


}

function(){
$A.componentService.addComponentClass("aura$expression", function() {


    var aura$component = $A.componentService.getComponentClass("aura$component");


    var aura$expression = function aura$expression(config, localCreation, creatingPrototype) {
        aura$component.call(this, config, localCreation, creatingPrototype); 
    };
    aura$expression.prototype = new aura$component(null, null, true);
    aura$expression.prototype.constructor = aura$expression;

    aura$expression.Helper = function() {};

    aura$expression.Helper.prototype = new aura$component.Helper();
    aura$expression.Helper.prototype.constructor = aura$expression.Helper;
    aura$expression.prototype.helper = new aura$expression.Helper();


    aura$expression.prototype.render = function() {
        var renderable = this.getRenderable();
        var component = renderable;
        var helper = renderable.helper;

		var value = component.get("v.value");
        if($A.util.isUndefinedOrNull(value)){
            value = "";
        }
        if(!($A.util.isComponent(value)||$A.util.isArray(value))){

            component._lastRenderedValue=value=$A.newCmp({componentDef:'aura:text', attributes:{values:{value:value}}});
        }
        return $A.renderingService.renderFacet(component,value);

    };

    aura$expression.prototype.rerender = function() {
        var renderable = this.getRenderable();
        var component = renderable;
        var helper = renderable.helper;

        var ret=[];
		if (component.isRendered()) {
            var value = component.get("v.value");
            if(!($A.util.isComponent(value)||$A.util.isArray(value))){
                if($A.util.isUndefinedOrNull(value)){
                    value = "";
                }
                if(component._lastRenderedValue && component._lastRenderedValue.isValid()){

                    component._lastRenderedValue.set("v.value",value,true);
                    value=component._lastRenderedValue;
                    return $A.rerender(value);
                }else {
                    value = $A.newCmp({componentDef: 'aura:text', attributes: {values: {value: value}}});
                }
            }
            ret=$A.renderingService.rerenderFacet(component, value);
        }
        return ret;

    };

    aura$expression.prototype.afterRender = function() {
        var renderable = this.getRenderable();
        var component = renderable;
        var helper = renderable.helper;

		var value = component.get("v.value");
		if ($A.util.isComponent(value)||$A.util.isArray(value)) {
			$A.afterRender(value);
		}

    };

    aura$expression.prototype.unrender = function() {
        var renderable = this.getRenderable();
        var component = renderable;
        var helper = renderable.helper;

        $A.renderingService.unrenderFacet(component);
        if (component._lastRenderedValue) {
            component._lastRenderedValue.destroy();
            component._lastRenderedValue = null;
        }

    };



    $A.Component.registerMethods(aura$expression);

    return aura$expression;
});


}

