// TODO: minify this
if (typeof Aura === "undefined" || !Aura.frameworkJsReady) {
    window.Aura = window.Aura || { frameworkLibrariesReady : true };
} else {
    window.Aura.frameworkLibrariesReady = true;
    var afterLibCallbacks = window.Aura.afterLibrariesLoaded;

    if (afterLibCallbacks && afterLibCallbacks.length) {
        for (var i = 0; i < afterLibCallbacks.length; i++) {
            afterLibCallbacks[i]();
        }
    }
}